import React from 'react';
import BestBooks from './BestBooks';
import Jumbotron from 'react-bootstrap/Jumbotron';
import './myFavoriteBooks.css';
import AddForm from './AddForm';
import axios from 'axios';
import { withAuth0 } from '@auth0/auth0-react';
import { Carousel, Container, Row, Col, CardColumns, Card, Button, Form, Modal } from 'react-bootstrap';


class MyFavoriteBooks extends React.Component {
  state = {
    data: [],
    show: false,
    close: true,
    index: 0,
    REname: '',
    Redescription: '',
    status:''
  }
  async componentDidMount() {
    const { user } = this.props.auth0;
    try {
      const bookURL = await fetch(`http://localhost:3001/books?email=${user.email}`)
      const resultOfBookURl = await bookURL.json();
      // console.log(resultOfBookURl)
      this.setState({
        data: resultOfBookURl
      })
    } catch {

    }
  };

  addBook = async (event) => {
    event.preventDefault();
    const { user } = this.props.auth0;
    const bookObj = {
      name: event.target.name.value,
      description: event.target.description.value,
      email: user.email
    }
    const newBook = await axios.post(`http://localhost:3001/addBook`, bookObj)
    console.log(bookObj);
    console.log(newBook.data);
    this.setState({
      data: newBook.data

    })

  };

  deletFunction = async (index) => {

    const { user } = this.props.auth0;
    const ownerEmail = {
      email: user.email
    }
    let newBookAfterdelet = await axios.delete(`http://localhost:3001/deletebook/${index}`, { params: ownerEmail })
    // console.log('ahmad')
    // console.log(newBookAfterdelet.data)

    this.setState({
      data: newBookAfterdelet.data
    })
  }

  updateShowModal = (index) => {
    this.setState({
      show: true,
      index: index,
      REname: this.state.data[index].name,
      Redescription: this.state.data[index].description,

    })
  }
  updatecloseModal = () => {
    this.setState({
      show: false
    })
  }
  udateBookProp = async (event) => {
    event.preventDefault();

    const { user } = this.props.auth0;
    const update = {
      name: event.target.name.value,
      description: event.target.description.value,
      email: user.email,
    }
    console.log(update)
    const updateBook = await axios.put(`http://localhost:3001/updateBook/${this.state.index}`, update)
    console.log(updateBook.data)
    this.setState({
      data: updateBook.data,
      show: false
    })
  }
  updateName = (event) => {
    this.setState({
      REname: event.target.name.value,
    })
  }
  updateDescription = (event) => {
    this.setState({
      Redescription: event.target.name.value,
    })
  }
  UpdateSelectionStatus=(event)=>{
    let newS
    // this.setState({
    //   status: event.target.select.value,
    // })
  }

  render() {


    return (
      <div  >
        <h1>My Favorite Books</h1>
        <p>
          This is a collection of my favorite books
        </p>
        <Modal show={this.state.show} onHide={this.updatecloseModal} >

          <Modal.Header closeButton>
            <Modal.Title>Update Your Book</Modal.Title>
          </Modal.Header>

          <Modal.Body>
            <Form onSubmit={(e) => this.udateBookProp(e)}>

              <Form.Group className="mb-3" controlId="formGroupEmail">
                <Form.Label>Book Name</Form.Label>
                <Form.Control type='text' placeholder="Enter Book Name" name='name' onChange={(e) => this.updateName(e)} value={this.state.REname} />
              </Form.Group>

              <Form.Group className="mb-3" controlId="formGroupPassword">
                <Form.Label>Book Description</Form.Label>
                <Form.Control type="text" placeholder="Description" name='description' onChange={(e) => this.updateDescription(e)} value={this.state.Redescription} />
              </Form.Group>

              <Form.Group controlId="exampleForm.SelectCustom">
                <Form.Control as="select" custom name='select' onChange={this.UpdateSelectionStatus} style={{ width: '100%', height: '3rem', marginBottom: '1rem' }}>
                  <option value=''></option>
                  <option value='1'>Life Changing</option>
                  <option value='2'>Favorite Five</option>
                  <option value='3'>Reccomended To Me</option>
                </Form.Control>
              </Form.Group>

              <Form.Group>
                <Button type="submit" variant="secondary" onSubmit={(e) => this.udateBookProp(e)} style={{ width: '100%' }}>Submit</Button>
              </Form.Group>

            </Form>
          </Modal.Body>

          <Modal.Footer>
            <Button variant="secondary" onClick={this.updatecloseModal} >Close</Button>
          </Modal.Footer>

        </Modal>



        <AddForm
          addBookProp={this.addBook}
        />
        {/* <button onClick={()=>this.props.deleteCatProps(idx)}>Delete</button> */}
        {/* <BestBooks/> */}

        <CardColumns>
          {this.state.data.map((item, idx) => {
            return (
              <Card style={{ width: '18rem' }}>
                <Card.Body>
                  <Card.Title>{item.name}</Card.Title>
                  <Card.Text>
                    {item.description}
                  </Card.Text>
                  <Button variant="primary" onClick={() => this.deletFunction(idx)} >Delete</Button>
                  <Button variant="primary" onClick={() => this.updateShowModal(idx)} >Update</Button>
                </Card.Body>
                {/* <Card.Footer>{this.state.status}</Card.Footer> */}
              </Card>
            )
          })}
        </CardColumns>


        {/* <Container style={{ width: '35rem' }}>
                    <Row>
                        <Col> */}
        {/* <Carousel style={{ width: '35rem' }}>
                                {this.state.data.map((item) => {
                                    return (
                                        <Carousel.Item interval={1000}>
                                            <img
                                                className="d-block w-100"
                                                src={item.img}
                                                alt="First slide"

                                            />
                                            <Carousel.Caption style={{ backgroundColor: 'rgba(96, 96, 96,.8) ' }}>
                                                <h3>{item.name}</h3>
                                                <p>{item.description}</p>
                                            </Carousel.Caption>
                                        </Carousel.Item>

                                    )
                                })}

                            </Carousel> */}
        {/* </Col>
                    </Row>
                </Container> */}
      </div>

    )
  }
}

export default withAuth0(MyFavoriteBooks);
