                <Profile/>
                </Route>
                {/* TODO: add a route with a path of '/profile' that renders a `Profile` component */}

              </Switch>
            <Footer />
          {/* </IsLoadingAndError> */}
        </Router>
      </>
    )
  }
}

export default withAuth0(App);
